CREATE TABLE `tbl_crop` (
  `cropid` int NOT NULL AUTO_INCREMENT,
  `cropname` varchar(70) NOT NULL,
  `croptypeid` varchar(50) DEFAULT NULL,
  `cropimg` varchar(200) NOT NULL,
  `climate` varchar(1000) DEFAULT NULL,
  `timeperiod` varchar(1000) DEFAULT NULL,
  `soilrequirement` varchar(1000) DEFAULT NULL,
  `landpreparation` varchar(1000) DEFAULT NULL,
  `sowing` varchar(1000) DEFAULT NULL,
  `irrigation` varchar(1000) DEFAULT NULL,
  `cropdesc` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`cropid`)
